import React from 'react';
import { Helmet } from 'react-helmet-async';
import { useLanguage } from '../../contexts/LanguageContext';

const SEO = ({ 
  title, 
  description, 
  keywords = [], 
  image, 
  type = 'website',
  url
}) => {
  const { currentLanguage } = useLanguage();
  const siteName = 'Vanguard Machinery';
  const baseUrl = 'https://vanguardmachinery.com';

  // Default values
  const defaultDescription = 'Leading manufacturer of cold roll forming machines with 15+ years of expertise, serving 165+ countries worldwide.';
  const defaultKeywords = ['cold roll forming', 'machinery', 'manufacturing', 'industrial equipment', 'CNC machines'];
  const defaultImage = `${baseUrl}/og-image.jpg`; // Ensure this exists or use a valid placeholder

  const metaTitle = title ? `${title} | ${siteName}` : siteName;
  const metaDescription = description || defaultDescription;
  const metaKeywords = keywords.length > 0 ? keywords.join(', ') : defaultKeywords.join(', ');
  const metaImage = image || defaultImage;
  const metaUrl = url ? `${baseUrl}${url}` : baseUrl;

  return (
    <Helmet>
      {/* Basic Meta Tags */}
      <html lang={currentLanguage} />
      <title>{metaTitle}</title>
      <meta name="description" content={metaDescription} />
      <meta name="keywords" content={metaKeywords} />

      {/* Open Graph / Facebook */}
      <meta property="og:type" content={type} />
      <meta property="og:url" content={metaUrl} />
      <meta property="og:title" content={metaTitle} />
      <meta property="og:description" content={metaDescription} />
      <meta property="og:image" content={metaImage} />
      <meta property="og:site_name" content={siteName} />
      <meta property="og:locale" content={currentLanguage} />

      {/* Twitter */}
      <meta name="twitter:card" content="summary_large_image" />
      <meta name="twitter:url" content={metaUrl} />
      <meta name="twitter:title" content={metaTitle} />
      <meta name="twitter:description" content={metaDescription} />
      <meta name="twitter:image" content={metaImage} />

      {/* Canonical */}
      <link rel="canonical" href={metaUrl} />
    </Helmet>
  );
};

export default SEO;
