import React from 'react';
import { 
  Truck, 
  Shield, 
  Headphones, 
  Tool, 
  Globe, 
  Award 
} from 'lucide-react';

const ServiceHighlights = () => {
  const services = [
    {
      icon: Truck,
      title: "Global Shipping",
      description: "Worldwide delivery with customs clearance support"
    },
    {
      icon: Shield,
      title: "Quality Guarantee",
      description: "All machines come with comprehensive warranties"
    },
    {
      icon: Headphones,
      title: "24/7 Support",
      description: "Round-the-clock technical assistance and customer service"
    },
    {
      icon: Tool,
      title: "Installation & Training",
      description: "Professional setup and operator training included"
    },
    {
      icon: Globe,
      title: "International Service",
      description: "Local service centers in major markets worldwide"
    },
    {
      icon: Award,
      title: "Certified Equipment",
      description: "All machinery meets international safety standards"
    }
  ];

  return (
    <section className="py-16 bg-white">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        <div className="text-center mb-12">
          <h2 className="text-3xl font-bold text-gray-900 mb-4">
            Why Choose Vanguard Machinery?
          </h2>
          <p className="text-lg text-gray-600 max-w-2xl mx-auto">
            We provide comprehensive solutions that go beyond just selling machinery
          </p>
        </div>

        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-8">
          {services.map((service, index) => (
            <div key={index} className="text-center p-6 rounded-lg hover:bg-gray-50 transition-colors duration-200">
              <div className="bg-blue-100 rounded-full w-16 h-16 flex items-center justify-center mx-auto mb-4">
                <service.icon className="w-8 h-8 text-blue-600" />
              </div>
              <h3 className="text-xl font-semibold text-gray-900 mb-2">
                {service.title}
              </h3>
              <p className="text-gray-600">
                {service.description}
              </p>
            </div>
          ))}
        </div>
      </div>
    </section>
  );
};

export default ServiceHighlights;
