import React, { useEffect, useState, useRef } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import { useAuth } from '../contexts/AuthContext';
import api, { getContactInquiries } from '../services/api';

const Chat = () => {
  const { user } = useAuth();
  const location = useLocation();
  const navigate = useNavigate();
  const [thread, setThread] = useState(null);
  const [input, setInput] = useState('');
  const [loading, setLoading] = useState(true);
  const pollRef = useRef(null);

  useEffect(() => {
    if (!user) {
      navigate('/login', { state: { from: { pathname: '/chat' + location.search } } });
      return;
    }
    const params = new URLSearchParams(location.search);
    const subject = params.get('product') ? `咨询产品：${params.get('product')}` : '聊天';
    const start = async () => {
      try {
        const res = await api.post('/contact/threads', { subject });
        setThread(res.data);
        setLoading(false);
      } catch (e) {
        setLoading(false);
      }
    };
    start();
  }, [user, location.search]);

  useEffect(() => {
    if (!thread) return;
    const poll = async () => {
      try {
        const res = await api.get(`/contact/threads/${thread.id}`);
        setThread(res.data);
      } catch {}
    };
    pollRef.current = setInterval(poll, 3000);
    return () => clearInterval(pollRef.current);
  }, [thread?.id]);

  const send = async () => {
    const text = input.trim();
    if (!text || !thread) return;
    try {
      await api.post(`/contact/${thread.id}/messages`, { text });
      setInput('');
    } catch {}
  };

  if (loading) return <div className="min-h-screen flex items-center justify-center">加载中...</div>;
  if (!thread) return <div className="min-h-screen flex items-center justify-center">无法创建会话</div>;

  const msgs = Array.isArray(thread.messages) ? thread.messages : [];

  return (
    <div className="min-h-screen bg-gray-50">
      <div className="max-w-4xl mx-auto p-4">
        <div className="bg-white rounded-lg shadow flex flex-col h-[80vh]">
          <div className="p-4 border-b font-semibold">与管理员聊天 - {thread.subject}</div>
          <div className="flex-1 overflow-y-auto p-4 space-y-3">
            {msgs.map((m, i) => (
              <div key={i} className={`flex ${m.from === 'user' ? 'justify-end' : 'justify-start'}`}>
                <div className={`px-3 py-2 rounded-lg max-w-[70%] ${m.from === 'user' ? 'bg-blue-600 text-white' : 'bg-gray-100 text-gray-900'}`}>
                  <div className="whitespace-pre-line">{m.text}</div>
                  <div className="text-xs opacity-70 mt-1">{new Date(m.at || Date.now()).toLocaleString()}</div>
                </div>
              </div>
            ))}
          </div>
          <div className="p-3 border-t flex space-x-2">
            <input value={input} onChange={e => setInput(e.target.value)} onKeyDown={e => e.key === 'Enter' ? send() : null} className="flex-1 border rounded-lg px-3 py-2" placeholder="输入消息..." />
            <button onClick={send} className="px-4 py-2 bg-blue-600 text-white rounded-lg">发送</button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Chat;
