import React, { useState, useEffect } from 'react';
import { motion } from 'framer-motion';
import { useLanguage } from '../contexts/LanguageContext';
import { 
  Calendar, 
  User, 
  Tag, 
  ArrowRight,
  Search,
  Filter
} from 'lucide-react';

const News = () => {
  const { t } = useLanguage();
  const [news, setNews] = useState([]);
  const [filteredNews, setFilteredNews] = useState([]);
  const [loading, setLoading] = useState(true);
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedCategory, setSelectedCategory] = useState('all');

  const categories = [
    { id: 'all', name: 'All Categories' },
    { id: 'company', name: 'Company News' },
    { id: 'industry', name: 'Industry Updates' },
    { id: 'technology', name: 'Technology' },
    { id: 'events', name: 'Events' }
  ];

  // Mock news data
  const mockNews = [
    {
      id: 1,
      title: 'Vanguard Machinery Expands to New Markets',
      excerpt: 'We are excited to announce our expansion into three new European markets, bringing our total coverage to 168 countries worldwide.',
      content: 'Vanguard Machinery has successfully expanded its operations into three new European markets: Poland, Czech Republic, and Hungary. This expansion represents a significant milestone in our global growth strategy and brings our total market coverage to 168 countries worldwide. Our new offices in Warsaw, Prague, and Budapest will provide local support and faster delivery times for customers in these regions.',
      category: 'company',
      author: 'John Smith',
      date: '2024-01-15',
      image: 'https://images.unsplash.com/photo-1581091226825-a6a2a5aee158?w=400&h=250&fit=crop',
      tags: ['expansion', 'europe', 'growth']
    },
    {
      id: 2,
      title: 'New AI-Powered CNC Machines Launch',
      excerpt: 'Introducing our latest generation of AI-powered CNC machines with advanced predictive maintenance and quality control features.',
      content: 'We are proud to announce the launch of our latest generation of AI-powered CNC machines. These revolutionary machines feature advanced predictive maintenance algorithms, real-time quality control monitoring, and adaptive cutting parameters that automatically adjust based on material properties and tool wear. The new series includes 5-axis milling machines, precision lathes, and multi-function machining centers.',
      category: 'technology',
      author: 'Sarah Johnson',
      date: '2024-01-10',
      image: 'https://images.unsplash.com/photo-1581091226825-a6a2a5aee158?w=400&h=250&fit=crop',
      tags: ['AI', 'CNC', 'innovation']
    },
    {
      id: 3,
      title: 'Industry 4.0 Conference Highlights',
      excerpt: 'Key insights from the recent Industry 4.0 conference where Vanguard Machinery showcased its latest smart factory solutions.',
      content: 'The recent Industry 4.0 conference in Berlin was a tremendous success, with Vanguard Machinery showcasing our latest smart factory solutions. Our booth featured live demonstrations of IoT-enabled machinery, real-time production monitoring systems, and AI-driven quality control solutions. The event attracted over 5,000 industry professionals from around the world, and we received numerous inquiries about our innovative technologies.',
      category: 'events',
      author: 'Michael Chen',
      date: '2024-01-05',
      image: 'https://images.unsplash.com/photo-1581091226825-a6a2a5aee158?w=400&h=250&fit=crop',
      tags: ['Industry 4.0', 'conference', 'smart factory']
    },
    {
      id: 4,
      title: 'Sustainability Initiatives in Manufacturing',
      excerpt: 'How Vanguard Machinery is leading the way in sustainable manufacturing practices and green technology adoption.',
      content: 'Sustainability is at the core of our business philosophy. We are proud to announce several new initiatives aimed at reducing our environmental impact and helping our customers achieve their sustainability goals. Our new line of energy-efficient machinery reduces power consumption by up to 30%, while our recycling programs ensure that 95% of our manufacturing waste is repurposed or recycled.',
      category: 'industry',
      author: 'Emily Davis',
      date: '2023-12-28',
      image: 'https://images.unsplash.com/photo-1581091226825-a6a2a5aee158?w=400&h=250&fit=crop',
      tags: ['sustainability', 'green tech', 'manufacturing']
    },
    {
      id: 5,
      title: 'Customer Success Story: Automotive Manufacturer',
      excerpt: 'How a leading automotive manufacturer increased production efficiency by 40% using Vanguard Machinery solutions.',
      content: 'We are excited to share the success story of one of our valued customers, a leading automotive manufacturer in Germany. By implementing our comprehensive machinery solutions, including advanced CNC systems and automated quality control, they achieved a 40% increase in production efficiency and a 25% reduction in defect rates. This case study demonstrates the real-world impact of our technology on manufacturing operations.',
      category: 'company',
      author: 'David Wilson',
      date: '2023-12-20',
      image: 'https://images.unsplash.com/photo-1581091226825-a6a2a5aee158?w=400&h=250&fit=crop',
      tags: ['success story', 'automotive', 'efficiency']
    },
    {
      id: 6,
      title: 'Future of Manufacturing: 2024 Trends',
      excerpt: 'Expert analysis of the key manufacturing trends that will shape the industry in 2024 and beyond.',
      content: 'As we look ahead to 2024, several key trends are emerging that will shape the future of manufacturing. Artificial intelligence and machine learning will continue to revolutionize production processes, while the Internet of Things (IoT) will enable unprecedented levels of connectivity and data collection. Additive manufacturing, also known as 3D printing, will become more mainstream for production applications, and sustainability will remain a top priority for manufacturers worldwide.',
      category: 'industry',
      author: 'Lisa Brown',
      date: '2023-12-15',
      image: 'https://images.unsplash.com/photo-1581091226825-a6a2a5aee158?w=400&h=250&fit=crop',
      tags: ['trends', '2024', 'manufacturing']
    }
  ];

  useEffect(() => {
    // Simulate API call
    setTimeout(() => {
      setNews(mockNews);
      setFilteredNews(mockNews);
      setLoading(false);
    }, 1000);
  }, []);

  useEffect(() => {
    let filtered = news;

    // Filter by search term
    if (searchTerm) {
      filtered = filtered.filter(article =>
        article.title.toLowerCase().includes(searchTerm.toLowerCase()) ||
        article.excerpt.toLowerCase().includes(searchTerm.toLowerCase()) ||
        article.content.toLowerCase().includes(searchTerm.toLowerCase())
      );
    }

    // Filter by category
    if (selectedCategory !== 'all') {
      filtered = filtered.filter(article => article.category === selectedCategory);
    }

    setFilteredNews(filtered);
  }, [news, searchTerm, selectedCategory]);

  const formatDate = (dateString) => {
    const date = new Date(dateString);
    return date.toLocaleDateString('en-US', {
      year: 'numeric',
      month: 'long',
      day: 'numeric'
    });
  };

  if (loading) {
    return (
      <div className="min-h-screen bg-gray-50 flex items-center justify-center">
        <div className="text-center">
          <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-600 mx-auto mb-4"></div>
          <p className="text-gray-600">Loading news...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-50">
      {/* Hero Section */}
      <section className="bg-gradient-to-r from-blue-900 to-gray-900 text-white py-16">
        <div className="container mx-auto px-4">
          <motion.div
            initial={{ opacity: 0, y: 30 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.8 }}
            className="text-center max-w-4xl mx-auto"
          >
            <h1 className="text-4xl md:text-5xl font-bold mb-6">
              Latest News & Updates
            </h1>
            <p className="text-xl text-gray-300">
              Stay informed about the latest developments in machinery technology and industry trends
            </p>
          </motion.div>
        </div>
      </section>

      {/* Filters and Search */}
      <section className="py-8 bg-white border-b">
        <div className="container mx-auto px-4">
          <div className="flex flex-col lg:flex-row gap-6 items-center justify-between">
            {/* Search */}
            <div className="relative flex-1 max-w-md">
              <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400 w-5 h-5" />
              <input
                type="text"
                placeholder="Search news articles..."
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
                className="w-full pl-10 pr-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
              />
            </div>

            {/* Category Filter */}
            <div className="flex items-center space-x-4">
              <Filter className="w-5 h-5 text-gray-600" />
              <select
                value={selectedCategory}
                onChange={(e) => setSelectedCategory(e.target.value)}
                className="px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
              >
                {categories.map(category => (
                  <option key={category.id} value={category.id}>
                    {category.name}
                  </option>
                ))}
              </select>
            </div>
          </div>
        </div>
      </section>

      {/* News Grid */}
      <section className="py-12">
        <div className="container mx-auto px-4">
          {filteredNews.length === 0 ? (
            <div className="text-center py-12">
              <p className="text-gray-600 text-lg">No news articles found matching your criteria.</p>
            </div>
          ) : (
            <div className="grid gap-8 md:grid-cols-2 lg:grid-cols-3">
              {filteredNews.map((article, index) => (
                <motion.article
                  key={article.id}
                  initial={{ opacity: 0, y: 20 }}
                  animate={{ opacity: 1, y: 0 }}
                  transition={{ duration: 0.6, delay: index * 0.1 }}
                  className="bg-white rounded-lg shadow-lg overflow-hidden hover:shadow-xl transition-shadow"
                >
                  {/* Article Image */}
                  <div className="relative">
                    <img
                      src={article.image}
                      alt={article.title}
                      className="w-full h-48 object-cover"
                    />
                    <div className="absolute top-4 left-4">
                      <span className="px-3 py-1 bg-blue-600 text-white text-sm font-medium rounded-full">
                        {categories.find(cat => cat.id === article.category)?.name}
                      </span>
                    </div>
                  </div>

                  {/* Article Content */}
                  <div className="p-6">
                    <div className="flex items-center text-sm text-gray-500 mb-3">
                      <Calendar className="w-4 h-4 mr-2" />
                      <span>{formatDate(article.date)}</span>
                      <span className="mx-2">â€¢</span>
                      <User className="w-4 h-4 mr-2" />
                      <span>{article.author}</span>
                    </div>

                    <h3 className="text-xl font-semibold text-gray-900 mb-3 line-clamp-2">
                      {article.title}
                    </h3>

                    <p className="text-gray-600 mb-4 line-clamp-3">
                      {article.excerpt}
                    </p>

                    {/* Tags */}
                    <div className="flex flex-wrap gap-2 mb-4">
                      {article.tags.map((tag, i) => (
                        <span
                          key={i}
                          className="px-2 py-1 bg-gray-100 text-gray-600 text-xs rounded"
                        >
                          #{tag}
                        </span>
                      ))}
                    </div>

                    <button className="inline-flex items-center text-blue-600 hover:text-blue-700 font-medium transition-colors">
                      Read More
                      <ArrowRight className="w-4 h-4 ml-2" />
                    </button>
                  </div>
                </motion.article>
              ))}
            </div>
          )}
        </div>
      </section>

      {/* Newsletter Signup */}
      <section className="py-16 bg-blue-900 text-white">
        <div className="container mx-auto px-4 text-center">
          <motion.div
            initial={{ opacity: 0, y: 30 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.8 }}
          >
            <h2 className="text-3xl md:text-4xl font-bold mb-6">
              Stay Updated
            </h2>
            <p className="text-xl text-gray-300 mb-8 max-w-2xl mx-auto">
              Subscribe to our newsletter to receive the latest news, industry insights, 
              and exclusive updates about new products and technologies.
            </p>
            <div className="flex flex-col sm:flex-row gap-4 justify-center max-w-md mx-auto">
              <input
                type="email"
                placeholder="Enter your email address"
                className="flex-1 px-4 py-3 text-gray-900 rounded-lg focus:ring-2 focus:ring-blue-500 focus:outline-none"
              />
              <button className="px-6 py-3 bg-white text-blue-900 font-semibold rounded-lg hover:bg-gray-100 transition-colors">
                Subscribe
              </button>
            </div>
          </motion.div>
        </div>
      </section>
    </div>
  );
};

export default News;
