  import React, { useState, useEffect } from 'react';
import { motion } from 'framer-motion';
import { useLanguage } from '../contexts/LanguageContext';
import { uploadFile, createProduct, createNews } from '../services/api';
import { 
  LayoutDashboard,
  Package,
  FileText,
  Users,
  MessageSquare,
  Settings,
  BarChart3,
  Plus,
  Edit,
  Trash2,
  Eye,
  Search,
  Filter,
  Download,
  Upload
} from 'lucide-react';

const Admin = () => {
  const { t } = useLanguage();
  const [activeTab, setActiveTab] = useState('dashboard');
  const [products, setProducts] = useState([]);
  const [news, setNews] = useState([]);
  const [contacts, setContacts] = useState([]);
  const [users, setUsers] = useState([]);
  const [showProductModal, setShowProductModal] = useState(false);
  const [showNewsModal, setShowNewsModal] = useState(false);
  const [showUserModal, setShowUserModal] = useState(false);
  const [editingItem, setEditingItem] = useState(null);
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedCategory, setSelectedCategory] = useState('all');
  const [selectedStatus, setSelectedStatus] = useState('all');
  const [recentActivities, setRecentActivities] = useState([
    { type: 'product', action: '添加了新产品', item: 'CNC加工中心', time: '2分钟前' },
    { type: 'news', action: '发布了新闻', item: '公司获得ISO认证', time: '1小时前' },
    { type: 'contact', action: '回复了咨询', item: '来自德国的询价', time: '3小时前' },
    { type: 'user', action: '新用户注册', item: 'John Smith', time: '5小时前' }
  ]);

  // 添加活动记录的函数
  const addActivity = (type, action, item) => {
    const now = new Date();
    const timeAgo = '刚刚';
    
    const newActivity = {
      type,
      action,
      item,
      time: timeAgo
    };
    
    setRecentActivities(prev => [newActivity, ...prev.slice(0, 3)]);
  };

  // 模拟数据
  const mockProducts = [
    {
      id: 1,
      name: 'CNC加工中心',
      model: 'VMC-850',
      category: 'CNC Machines',
      price: 125000,
      status: 'published',
      image: '/api/placeholder/150/150',
      createdAt: '2024-01-15'
    },
    {
      id: 2,
      name: '工业机器人',
      model: 'IRB-1200',
      category: 'Automation Systems',
      price: 89000,
      status: 'published',
      image: '/api/placeholder/150/150',
      createdAt: '2024-01-10'
    },
    {
      id: 3,
      name: '精密测量仪',
      model: 'CMM-2000',
      category: 'Quality Control',
      price: 156000,
      status: 'draft',
      image: '/api/placeholder/150/150',
      createdAt: '2024-01-08'
    }
  ];

  const mockNews = [
    {
      id: 1,
      title: '公司获得ISO 9001认证',
      category: 'Company News',
      status: 'published',
      author: 'Admin',
      publishDate: '2024-01-20',
      viewCount: 156
    },
    {
      id: 2,
      title: '新产品发布会成功举办',
      category: 'Product Launches',
      status: 'published',
      author: 'Admin',
      publishDate: '2024-01-18',
      viewCount: 89
    }
  ];

  const mockContacts = [
    {
      id: 1,
      name: 'John Smith',
      email: 'john@example.com',
      company: 'ABC Manufacturing',
      subject: 'CNC机床询价',
      status: 'new',
      priority: 'high',
      createdAt: '2024-01-20'
    },
    {
      id: 2,
      name: 'Maria Garcia',
      email: 'maria@example.com',
      company: 'XYZ Industries',
      subject: '技术支持请求',
      status: 'in_progress',
      priority: 'medium',
      createdAt: '2024-01-19'
    }
  ];

  const mockUsers = [
    {
      id: 1,
      username: 'admin',
      email: 'admin@vanguard.com',
      role: 'admin',
      status: 'active',
      lastLogin: '2024-01-20 10:30:00'
    }
  ];

  // 初始化数据
  React.useEffect(() => {
    setProducts(mockProducts);
    setNews(mockNews);
    setContacts(mockContacts);
    setUsers(mockUsers);
  }, []);

  // 产品管理函数
  const handleAddProduct = () => {
    setEditingItem(null);
    setShowProductModal(true);
  };

  const handleEditProduct = (product) => {
    setEditingItem(product);
    setShowProductModal(true);
  };

  const handleDeleteProduct = (productId) => {
    if (window.confirm('确定要删除这个产品吗？')) {
      setProducts(products.filter(p => p.id !== productId));
    }
  };

  // 更新产品提交函数，添加活动记录
  const handleProductSubmit = async (productData) => {
    try {
      // 验证必填字段
      if (!productData.name || !productData.description || !productData.category) {
        throw new Error('请填写所有必填字段');
      }
      
      // 生成slug
      const slug = productData.name
        .toLowerCase()
        .replace(/[^a-z0-9]+/g, '-')
        .replace(/(^-|-$)/g, '');
      
      // 处理price字段
      let price = null;
      if (productData.price && !isNaN(parseFloat(productData.price))) {
        // 确保价格在合理范围内 (最多10位数字，2位小数)
        price = Math.min(parseFloat(productData.price), 99999999.99);
        price = parseFloat(price.toFixed(2));
      }
      
      // 确保category字段有效
      const validCategories = ['CNC Machines', 'Industrial Equipment', 'Manufacturing Tools', 'Automation Systems', 'Quality Control'];
      const category = validCategories.includes(productData.category) ? productData.category : 'CNC Machines';
      
      // 调整数据格式以匹配API要求
      const apiProductData = {
        name: productData.name,
        category: category, // 确保category字段有效
        description: productData.description || '暂无描述',
        mainImage: productData.image || '/api/placeholder/400/300',
        price: price,
        isPublished: productData.status === 'published',
        isFeatured: false,
        slug: slug,
        images: [],
        seoKeywords: []
      };

      console.log('Sending product data:', apiProductData); // 添加日志
      
      if (editingItem) {
        // 编辑现有产品（简化处理，实际应该调用更新API）
        setProducts(products.map(p => 
          p.id === editingItem.id ? { ...p, ...productData } : p
        ));
        addActivity('product', '编辑了产品', productData.name);
      } else {
        // 添加新产品到数据库
        const newProduct = await createProduct(apiProductData);
        
        setProducts([...products, newProduct]);
        addActivity('product', '添加了新产品', productData.name);
      }
      setShowProductModal(false);
      setEditingItem(null);
    } catch (error) {
      console.error('创建产品失败:', error);
      // 可以添加错误提示给用户
      alert('创建产品失败，请检查所有必填字段是否已填写');
      // 添加详细的错误日志
      if (error.response && error.response.data && error.response.data.errors) {
        console.error('验证错误:', error.response.data.errors);
      }
    }
  };

  // 新闻管理函数
  const handleAddNews = () => {
    setEditingItem(null);
    setShowNewsModal(true);
  };

  const handleEditNews = (news) => {
    setEditingItem(news);
    setShowNewsModal(true);
  };

  const handleDeleteNews = (newsId) => {
    if (window.confirm('确定要删除这篇新闻吗？')) {
      setNews(news.filter(n => n.id !== newsId));
    }
  };

  // 更新新闻提交函数，添加活动记录
  const handleNewsSubmit = async (newsData) => {
    try {
      if (editingItem) {
        // 编辑现有新闻（简化处理，实际应该调用更新API）
        setNews(news.map(n => 
          n.id === editingItem.id ? { ...n, ...newsData } : n
        ));
        addActivity('news', '编辑了新闻', newsData.title);
      } else {
        // 添加新闻到数据库
        const newNews = await createNews({
          ...newsData,
          isPublished: true,
          summary: newsData.content.substring(0, 200) + '...', // 从内容生成摘要
          author: 'Admin' // 默认作者
        });
        
        setNews([...news, newNews]);
        addActivity('news', '发布了新闻', newsData.title);
      }
      setShowNewsModal(false);
      setEditingItem(null);
    } catch (error) {
      console.error('创建新闻失败:', error);
      // 可以添加错误提示给用户
    }
  };

  // 用户管理函数
  const handleAddUser = () => {
    setEditingItem(null);
    setShowUserModal(true);
  };

  const handleEditUser = (user) => {
    setEditingItem(user);
    setShowUserModal(true);
  };

  const handleDeleteUser = (userId) => {
    if (window.confirm('确定要删除这个用户吗？')) {
      setUsers(users.filter(u => u.id !== userId));
    }
  };

  // 更新用户提交函数，添加活动记录
  const handleUserSubmit = (userData) => {
    if (editingItem) {
      setUsers(users.map(u => 
        u.id === editingItem.id ? { ...u, ...userData } : u
      ));
      addActivity('user', '编辑了用户', userData.username);
    } else {
      const newUser = {
        id: Date.now(),
        ...userData,
        role: 'admin',
        status: 'active',
        lastLogin: null
      };
      setUsers([...users, newUser]);
      addActivity('user', '添加了新用户', userData.username);
    }
    setShowUserModal(false);
    setEditingItem(null);
  };

  // 联系管理函数
  const handleContactStatusChange = (contactId, newStatus) => {
    setContacts(contacts.map(c => 
      c.id === contactId ? { ...c, status: newStatus } : c
    ));
  };

  const handleContactPriorityChange = (contactId, newPriority) => {
    setContacts(contacts.map(c => 
      c.id === contactId ? { ...c, priority: newPriority } : c
    ));
  };

  // 筛选和搜索
  const filteredProducts = products.filter(product => {
    const matchesSearch = product.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
                         product.model.toLowerCase().includes(searchTerm.toLowerCase());
    const matchesCategory = selectedCategory === 'all' || product.category === selectedCategory;
    const matchesStatus = selectedStatus === 'all' || product.status === selectedStatus;
    return matchesSearch && matchesCategory && matchesStatus;
  });

  const menuItems = [
    { id: 'dashboard', label: '仪表板', icon: LayoutDashboard, color: 'blue' },
    { id: 'products', label: '产品管理', icon: Package, color: 'green' },
    { id: 'news', label: '新闻管理', icon: FileText, color: 'purple' },
    { id: 'users', label: '用户管理', icon: Users, color: 'orange' },
    { id: 'contacts', label: '联系管理', icon: MessageSquare, color: 'red' },
    { id: 'seo', label: 'SEO设置', icon: Settings, color: 'indigo' }
  ];

  const stats = [
    { label: '总产品数', value: products.length.toString(), change: '+12', changeType: 'positive' },
    { label: '总新闻数', value: news.length.toString(), change: '+5', changeType: 'positive' },
    { label: '总用户数', value: users.length.toString(), change: '+23', changeType: 'positive' },
    { label: '未读消息', value: contacts.filter(c => c.status === 'new').length.toString(), change: '-8', changeType: 'negative' }
  ];

  const renderDashboard = () => (
    <div className="space-y-6">
      {/* Stats Cards */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
        {stats.map((stat, index) => (
          <motion.div
            key={stat.label}
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.5, delay: index * 0.1 }}
            className="bg-white rounded-lg shadow-md p-6"
          >
            <div className="flex items-center justify-between">
              <div>
                <p className="text-sm font-medium text-gray-600">{stat.label}</p>
                <p className="text-2xl font-bold text-gray-900">{stat.value}</p>
              </div>
              <div className={`text-sm font-medium ${
                stat.changeType === 'positive' ? 'text-green-600' : 'text-red-600'
              }`}>
                {stat.change}
              </div>
            </div>
          </motion.div>
        ))}
      </div>

      {/* Recent Activities */}
      <div className="bg-white rounded-lg shadow-md p-6">
        <h3 className="text-lg font-semibold text-gray-900 mb-4">最近活动</h3>
        <div className="space-y-3">
          {recentActivities.map((activity, index) => (
            <motion.div
              key={index}
              initial={{ opacity: 0, x: -20 }}
              animate={{ opacity: 1, x: 0 }}
              transition={{ duration: 0.5, delay: index * 0.1 }}
              className="flex items-center space-x-3 p-3 bg-gray-50 rounded-lg"
            >
              <div className="w-2 h-2 bg-blue-500 rounded-full"></div>
              <div className="flex-1">
                <span className="font-medium">{activity.action}</span>
                <span className="text-gray-600">: {activity.item}</span>
              </div>
              <span className="text-sm text-gray-500">{activity.time}</span>
            </motion.div>
          ))}
        </div>
      </div>

      {/* Quick Actions */}
      <div className="bg-white rounded-lg shadow-md p-6">
        <h3 className="text-lg font-semibold text-gray-900 mb-4">快速操作</h3>
        <div className="grid grid-cols-2 md:grid-cols-4 gap-4">
          <button 
            onClick={handleAddProduct}
            className="flex flex-col items-center p-4 border border-gray-200 rounded-lg hover:border-blue-500 hover:bg-blue-50 transition-colors group"
          >
            <Plus className="w-8 h-8 text-blue-600 mb-2 group-hover:scale-110 transition-transform" />
            <span className="text-sm font-medium text-gray-700 group-hover:text-blue-700">添加产品</span>
          </button>
          <button 
            onClick={handleAddNews}
            className="flex flex-col items-center p-4 border border-gray-200 rounded-lg hover:border-green-500 hover:bg-green-50 transition-colors group"
          >
            <FileText className="w-8 h-8 text-green-600 mb-2 group-hover:scale-110 transition-transform" />
            <span className="text-sm font-medium text-gray-700 group-hover:text-green-700">发布新闻</span>
          </button>
          <button 
            onClick={handleAddUser}
            className="flex flex-col items-center p-4 border border-gray-200 rounded-lg hover:border-purple-500 hover:bg-purple-50 transition-colors group"
          >
            <Users className="w-8 h-8 text-purple-600 mb-2 group-hover:scale-110 transition-transform" />
            <span className="text-sm font-medium text-gray-700 group-hover:text-purple-700">管理用户</span>
          </button>
          <button 
            onClick={() => setActiveTab('seo')}
            className="flex flex-col items-center p-4 border border-gray-200 rounded-lg hover:border-orange-500 hover:bg-orange-50 transition-colors group"
