import React from 'react';
import { motion } from 'framer-motion';
import { Link } from 'react-router-dom';
import { ChevronRight, Play } from 'lucide-react';
import { useLanguage } from '../../contexts/LanguageContext';

const HeroSection = () => {
  const { t } = useLanguage();
  return (
    <section className="relative h-screen flex items-center justify-center overflow-hidden">
      {/* Background Image/Video */}
      <div className="absolute inset-0 z-0">
        <div className="absolute inset-0 bg-gradient-to-b from-black/70 via-black/50 to-black/80 z-10" />
        <div 
          className="w-full h-full bg-cover bg-center bg-no-repeat transform scale-105 animate-float"
          style={{
            backgroundImage: `url('https://images.unsplash.com/photo-1504917595217-d4dc5ebe6122?ixlib=rb-4.0.3&auto=format&fit=crop&w=2070&q=80')`
          }}
        />
      </div>

      {/* Content */}
      <div className="relative z-20 container mx-auto px-6 text-center text-white">
        <motion.div
          initial={{ opacity: 0, y: 50 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 1, ease: "easeOut" }}
          className="max-w-5xl mx-auto"
        >
          {/* Decorative Line */}
          <motion.div 
            initial={{ height: 0 }}
            animate={{ height: '4rem' }}
            transition={{ duration: 1, delay: 0.5 }}
            className="w-px bg-secondary-400 mx-auto mb-8"
          />

          {/* Main Title */}
          <motion.h1 
            className="text-5xl md:text-7xl lg:text-8xl font-serif font-bold mb-8 leading-tight tracking-tight"
          >
            <span className="block text-transparent bg-clip-text bg-gradient-to-b from-white to-white/80">
              {t('heroTitle')}
            </span>
            <span className="block text-secondary-400 italic mt-2 font-light">
              {t('heroSubtitle')}
            </span>
          </motion.h1>

          {/* Subtitle */}
          <motion.p 
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            transition={{ duration: 1, delay: 0.8 }}
            className="text-lg md:text-xl mb-12 text-gray-300 max-w-2xl mx-auto font-light leading-relaxed tracking-wide"
          >
            {t('heroDescription')}
          </motion.p>

          {/* CTA Buttons */}
          <motion.div 
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.8, delay: 1 }}
            className="flex flex-col sm:flex-row gap-6 justify-center items-center"
          >
            <Link
              to="/products"
              className="group relative px-10 py-4 bg-secondary-500 text-white font-medium tracking-widest uppercase text-sm overflow-hidden transition-all duration-300 hover:bg-secondary-600 hover:shadow-lg hover:shadow-secondary-500/30"
            >
              <span className="relative z-10 flex items-center">
                {t('exploreProducts')}
                <ChevronRight className="ml-2 w-4 h-4 group-hover:translate-x-1 transition-transform" />
              </span>
            </Link>
            
            <Link
              to="/contact"
              className="group px-10 py-4 border border-white/30 text-white font-medium tracking-widest uppercase text-sm transition-all duration-300 hover:bg-white hover:text-primary-900"
            >
              {t('getQuote')}
            </Link>
          </motion.div>
        </motion.div>
      </div>

      {/* Scroll Indicator */}
      <motion.div 
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        transition={{ delay: 2, duration: 1 }}
        className="absolute bottom-10 left-1/2 transform -translate-x-1/2 flex flex-col items-center z-20"
      >
        <span className="text-white/50 text-xs uppercase tracking-widest mb-2">Scroll</span>
        <div className="w-px h-12 bg-gradient-to-b from-white to-transparent" />
      </motion.div>
    </section>
  );
};

export default HeroSection;
