import React, { useState } from 'react';
import { motion } from 'framer-motion';
import { useLanguage } from '../contexts/LanguageContext';
import { 
  LayoutDashboard,
  Package,
  FileText,
  Users,
  MessageSquare,
  Settings,
  BarChart3,
  Plus,
  Edit,
  Trash2,
  Eye,
  Search,
  Filter,
  Download,
  Upload
} from 'lucide-react';

const Admin = () => {
  const { t } = useLanguage();
  const [activeTab, setActiveTab] = useState('dashboard');
  const [products, setProducts] = useState([]);
  const [news, setNews] = useState([]);
  const [contacts, setContacts] = useState([]);
  const [users, setUsers] = useState([]);
  const [showProductModal, setShowProductModal] = useState(false);
  const [showNewsModal, setShowNewsModal] = useState(false);
  const [showUserModal, setShowUserModal] = useState(false);
  const [editingItem, setEditingItem] = useState(null);
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedCategory, setSelectedCategory] = useState('all');
  const [selectedStatus, setSelectedStatus] = useState('all');

  // 模拟数据
  const mockProducts = [
    {
      id: 1,
      name: 'CNC加工中心',
      model: 'VMC-850',
      category: 'CNC Machines',
      price: 125000,
      status: 'published',
      image: '/api/placeholder/150/150',
      createdAt: '2024-01-15'
    },
    {
      id: 2,
      name: '工业机器人',
      model: 'IRB-1200',
      category: 'Automation Systems',
      price: 89000,
      status: 'published',
      image: '/api/placeholder/150/150',
      createdAt: '2024-01-10'
    },
    {
      id: 3,
      name: '精密测量仪',
      model: 'CMM-2000',
      category: 'Quality Control',
      price: 156000,
      status: 'draft',
      image: '/api/placeholder/150/150',
      createdAt: '2024-01-08'
    }
  ];

  const mockNews = [
    {
      id: 1,
      title: '公司获得ISO 9001认证',
      category: 'Company News',
      status: 'published',
      author: 'Admin',
      publishDate: '2024-01-20',
      viewCount: 156
    },
    {
      id: 2,
      title: '新产品发布会成功举办',
      category: 'Product Launches',
      status: 'published',
      author: 'Admin',
      publishDate: '2024-01-18',
      viewCount: 89
    }
  ];

  const mockContacts = [
    {
      id: 1,
      name: 'John Smith',
      email: 'john@example.com',
      company: 'ABC Manufacturing',
      subject: 'CNC机床询价',
      status: 'new',
      priority: 'high',
      createdAt: '2024-01-20'
    },
    {
      id: 2,
      name: 'Maria Garcia',
      email: 'maria@example.com',
      company: 'XYZ Industries',
      subject: '技术支持请求',
      status: 'in_progress',
      priority: 'medium',
      createdAt: '2024-01-19'
    }
  ];

  const mockUsers = [
    {
      id: 1,
      username: 'admin',
      email: 'admin@vanguard.com',
      role: 'admin',
      status: 'active',
      lastLogin: '2024-01-20 10:30:00'
    }
  ];

  // 初始化数据
  React.useEffect(() => {
    setProducts(mockProducts);
    setNews(mockNews);
    setContacts(mockContacts);
    setUsers(mockUsers);
  }, []);

  // 产品管理函数
  const handleAddProduct = () => {
    setEditingItem(null);
    setShowProductModal(true);
  };

  const handleEditProduct = (product) => {
    setEditingItem(product);
    setShowProductModal(true);
  };

  const handleDeleteProduct = (productId) => {
    if (window.confirm('确定要删除这个产品吗？')) {
      setProducts(products.filter(p => p.id !== productId));
    }
  };

  const handleProductSubmit = (productData) => {
    if (editingItem) {
      // 编辑现有产品
      setProducts(products.map(p => 
        p.id === editingItem.id ? { ...p, ...productData } : p
      ));
    } else {
      // 添加新产品
      const newProduct = {
        id: Date.now(),
        ...productData,
        createdAt: new Date().toISOString().split('T')[0]
      };
      setProducts([...products, newProduct]);
    }
    setShowProductModal(false);
    setEditingItem(null);
  };

  // 新闻管理函数
  const handleAddNews = () => {
    setEditingItem(null);
    setShowNewsModal(true);
  };

  const handleEditNews = (news) => {
    setEditingItem(news);
    setShowNewsModal(true);
  };

  const handleDeleteNews = (newsId) => {
    if (window.confirm('确定要删除这篇新闻吗？')) {
      setNews(news.filter(n => n.id !== newsId));
    }
  };

  const handleNewsSubmit = (newsData) => {
    if (editingItem) {
      setNews(news.map(n => 
        n.id === editingItem.id ? { ...n, ...newsData } : n
      ));
    } else {
      const newNews = {
        id: Date.now(),
        ...newsData,
        author: 'Admin',
        publishDate: new Date().toISOString().split('T')[0],
        viewCount: 0
      };
      setNews([...news, newNews]);
    }
    setShowNewsModal(false);
    setEditingItem(null);
  };

  // 用户管理函数
  const handleAddUser = () => {
    setEditingItem(null);
    setShowUserModal(true);
  };

  const handleEditUser = (user) => {
    setEditingItem(user);
    setShowUserModal(true);
  };

  const handleDeleteUser = (userId) => {
    if (window.confirm('确定要删除这个用户吗？')) {
      setUsers(users.filter(u => u.id !== userId));
    }
  };

  const handleUserSubmit = (userData) => {
    if (editingItem) {
      setUsers(users.map(u => 
        u.id === editingItem.id ? { ...u, ...userData } : u
      ));
    } else {
      const newUser = {
        id: Date.now(),
        ...userData,
        role: 'admin',
        status: 'active',
        lastLogin: null
      };
      setUsers([...users, newUser]);
    }
    setShowUserModal(false);
    setEditingItem(null);
  };

  // 联系管理函数
  const handleContactStatusChange = (contactId, newStatus) => {
    setContacts(contacts.map(c => 
      c.id === contactId ? { ...c, status: newStatus } : c
    ));
  };

  const handleContactPriorityChange = (contactId, newPriority) => {
    setContacts(contacts.map(c => 
      c.id === contactId ? { ...c, priority: newPriority } : c
    ));
  };

  // 筛选和搜索
  const filteredProducts = products.filter(product => {
    const matchesSearch = product.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
                         product.model.toLowerCase().includes(searchTerm.toLowerCase());
    const matchesCategory = selectedCategory === 'all' || product.category === selectedCategory;
    const matchesStatus = selectedStatus === 'all' || product.status === selectedStatus;
    return matchesSearch && matchesCategory && matchesStatus;
  });

  const menuItems = [
    { id: 'dashboard', label: '仪表板', icon: LayoutDashboard, color: 'blue' },
    { id: 'products', label: '产品管理', icon: Package, color: 'green' },
    { id: 'news', label: '新闻管理', icon: FileText, color: 'purple' },
    { id: 'users', label: '用户管理', icon: Users, color: 'orange' },
    { id: 'contacts', label: '联系管理', icon: MessageSquare, color: 'red' },
    { id: 'seo', label: 'SEO设置', icon: Settings, color: 'indigo' }
  ];

  const stats = [
    { label: '总产品数', value: '156', change: '+12', changeType: 'positive' },
    { label: '总新闻数', value: '89', change: '+5', changeType: 'positive' },
    { label: '总用户数', value: '2,847', change: '+23', changeType: 'positive' },
    { label: '未读消息', value: '23', change: '-8', changeType: 'negative' }
  ];

  const recentActivities = [
    { type: 'product', action: '添加了新产品', item: 'CNC加工中心', time: '2分钟前' },
    { type: 'news', action: '发布了新闻', item: '公司获得ISO认证', time: '1小时前' },
    { type: 'contact', action: '回复了咨询', item: '来自德国的询价', time: '3小时前' },
    { type: 'user', action: '新用户注册', item: 'John Smith', time: '5小时前' }
  ];

  const renderDashboard = () => (
    <div className="space-y-6">
      {/* Stats Cards */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
        {stats.map((stat, index) => (
          <motion.div
            key={stat.label}
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.5, delay: index * 0.1 }}
            className="bg-white rounded-lg shadow-md p-6"
          >
            <div className="flex items-center justify-between">
              <div>
                <p className="text-sm font-medium text-gray-600">{stat.label}</p>
                <p className="text-2xl font-bold text-gray-900">{stat.value}</p>
              </div>
              <div className={`text-sm font-medium ${
                stat.changeType === 'positive' ? 'text-green-600' : 'text-red-600'
              }`}>
                {stat.change}
              </div>
            </div>
          </motion.div>
        ))}
      </div>

      {/* Recent Activities */}
      <div className="bg-white rounded-lg shadow-md p-6">
        <h3 className="text-lg font-semibold text-gray-900 mb-4">最近活动</h3>
        <div className="space-y-3">
          {recentActivities.map((activity, index) => (
            <motion.div
              key={index}
              initial={{ opacity: 0, x: -20 }}
              animate={{ opacity: 1, x: 0 }}
              transition={{ duration: 0.5, delay: index * 0.1 }}
              className="flex items-center space-x-3 p-3 bg-gray-50 rounded-lg"
            >
              <div className="w-2 h-2 bg-blue-500 rounded-full"></div>
              <div className="flex-1">
                <span className="font-medium">{activity.action}</span>
                <span className="text-gray-600">: {activity.item}</span>
              </div>
              <span className="text-sm text-gray-500">{activity.time}</span>
            </motion.div>
          ))}
        </div>
      </div>

      {/* Quick Actions */}
      <div className="bg-white rounded-lg shadow-md p-6">
        <h3 className="text-lg font-semibold text-gray-900 mb-4">快速操作</h3>
        <div className="grid grid-cols-2 md:grid-cols-4 gap-4">
          <button className="flex flex-col items-center p-4 border border-gray-200 rounded-lg hover:border-blue-500 hover:bg-blue-50 transition-colors">
            <Plus className="w-8 h-8 text-blue-600 mb-2" />
            <span className="text-sm font-medium">添加产品</span>
          </button>
          <button className="flex flex-col items-center p-4 border border-gray-200 rounded-lg hover:border-green-500 hover:bg-green-50 transition-colors">
            <FileText className="w-8 h-8 text-green-600 mb-2" />
            <span className="text-sm font-medium">发布新闻</span>
          </button>
          <button className="flex flex-col items-center p-4 border border-gray-200 rounded-lg hover:border-purple-500 hover:bg-purple-50 transition-colors">
            <Users className="w-8 h-8 text-purple-600 mb-2" />
            <span className="text-sm font-medium">管理用户</span>
          </button>
          <button className="flex flex-col items-center p-4 border border-gray-200 rounded-lg hover:border-orange-500 hover:bg-orange-50 transition-colors">
            <Settings className="w-8 h-8 text-orange-600 mb-2" />
            <span className="text-sm font-medium">系统设置</span>
          </button>
        </div>
      </div>
    </div>
  );

  const renderProducts = () => (
    <div className="space-y-6">
      {/* Header */}
      <div className="flex flex-col sm:flex-row justify-between items-start sm:items-center space-y-4 sm:space-y-0">
        <div>
          <h2 className="text-2xl font-bold text-gray-900">产品管理</h2>
          <p className="text-gray-600">管理您的产品目录、价格和库存</p>
        </div>
        <button 
          onClick={handleAddProduct}
          className="bg-blue-600 text-white px-4 py-2 rounded-lg hover:bg-blue-700 transition-colors flex items-center space-x-2"
        >
          <Plus className="w-4 h-4" />
          <span>添加产品</span>
        </button>
      </div>

      {/* Filters */}
      <div className="bg-white rounded-lg shadow-md p-6">
        <div className="flex flex-col sm:flex-row gap-4">
          <div className="flex-1">
            <div className="relative">
              <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400 w-5 h-5" />
              <input
                type="text"
                placeholder="搜索产品..."
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
                className="w-full pl-10 pr-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
              />
            </div>
          </div>
          <select 
            value={selectedCategory}
            onChange={(e) => setSelectedCategory(e.target.value)}
            className="px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
          >
            <option value="all">所有分类</option>
            <option value="CNC Machines">CNC机床</option>
            <option value="Industrial Equipment">工业设备</option>
            <option value="Manufacturing Tools">制造工具</option>
            <option value="Automation Systems">自动化系统</option>
            <option value="Quality Control">质量控制</option>
          </select>
          <select 
            value={selectedStatus}
            onChange={(e) => setSelectedStatus(e.target.value)}
            className="px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
          >
            <option value="all">所有状态</option>
            <option value="published">已发布</option>
            <option value="draft">草稿</option>
            <option value="archived">已归档</option>
          </select>
        </div>
      </div>

      {/* Products Table */}
      <div className="bg-white rounded-lg shadow-md overflow-hidden">
        <div className="px-6 py-4 border-b border-gray-200">
          <h3 className="text-lg font-semibold text-gray-900">产品列表 ({filteredProducts.length})</h3>
        </div>
        <div className="overflow-x-auto">
          <table className="min-w-full divide-y divide-gray-200">
            <thead className="bg-gray-50">
              <tr>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">产品</th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">分类</th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">价格</th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">状态</th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">创建时间</th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">操作</th>
              </tr>
            </thead>
            <tbody className="bg-white divide-y divide-gray-200">
              {filteredProducts.map((product) => (
                <tr key={product.id}>
                  <td className="px-6 py-4 whitespace-nowrap">
                    <div className="flex items-center">
                      <div className="w-10 h-10 bg-gray-200 rounded-lg mr-3 flex-shrink-0"></div>
                      <div>
                        <div className="text-sm font-medium text-gray-900">{product.name}</div>
                        <div className="text-sm text-gray-500">{product.model}</div>
                      </div>
                    </div>
                  </td>
                  <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">{product.category}</td>
                  <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">¥{product.price.toLocaleString()}</td>
                  <td className="px-6 py-4 whitespace-nowrap">
                    <span className={`px-2 inline-flex text-xs leading-5 font-semibold rounded-full ${
                      product.status === 'published' ? 'bg-green-100 text-green-800' :
                      product.status === 'draft' ? 'bg-yellow-100 text-yellow-800' :
                      'bg-gray-100 text-gray-800'
                    }`}>
                      {product.status === 'published' ? '已发布' :
                       product.status === 'draft' ? '草稿' : '已归档'}
                    </span>
                  </td>
                  <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">{product.createdAt}</td>
                  <td className="px-6 py-4 whitespace-nowrap text-sm font-medium space-x-2">
                    <button 
                      onClick={() => handleEditProduct(product)}
                      className="text-blue-600 hover:text-blue-900"
                    >
                      编辑
                    </button>
                    <button 
                      onClick={() => handleDeleteProduct(product.id)}
                      className="text-red-600 hover:text-red-900"
                    >
                      删除
                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );

  const renderNews = () => (
    <div className="space-y-6">
      <div className="flex flex-col sm:flex-row justify-between items-start sm:items-center space-y-4 sm:space-y-0">
        <div>
          <h2 className="text-2xl font-bold text-gray-900">新闻管理</h2>
          <p className="text-gray-600">管理公司新闻和行业动态</p>
        </div>
        <button 
          onClick={handleAddNews}
          className="bg-green-600 text-white px-4 py-2 rounded-lg hover:bg-green-700 transition-colors flex items-center space-x-2"
        >
          <Plus className="w-4 h-4" />
          <span>发布新闻</span>
        </button>
      </div>

      {/* News Table */}
      <div className="bg-white rounded-lg shadow-md overflow-hidden">
        <div className="px-6 py-4 border-b border-gray-200">
          <h3 className="text-lg font-semibold text-gray-900">新闻列表 ({news.length})</h3>
        </div>
        <div className="overflow-x-auto">
          <table className="min-w-full divide-y divide-gray-200">
            <thead className="bg-gray-50">
              <tr>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">标题</th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">分类</th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">作者</th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">状态</th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">发布日期</th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">浏览量</th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">操作</th>
              </tr>
            </thead>
            <tbody className="bg-white divide-y divide-gray-200">
              {news.map((item) => (
                <tr key={item.id}>
                  <td className="px-6 py-4 whitespace-nowrap">
                    <div className="text-sm font-medium text-gray-900">{item.title}</div>
                  </td>
                  <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">{item.category}</td>
                  <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">{item.author}</td>
                  <td className="px-6 py-4 whitespace-nowrap">
                    <span className={`px-2 inline-flex text-xs leading-5 font-semibold rounded-full ${
                      item.status === 'published' ? 'bg-green-100 text-green-800' : 'bg-yellow-100 text-yellow-800'
                    }`}>
                      {item.status === 'published' ? '已发布' : '草稿'}
                    </span>
                  </td>
                  <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">{item.publishDate}</td>
                  <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">{item.viewCount}</td>
                  <td className="px-6 py-4 whitespace-nowrap text-sm font-medium space-x-2">
                    <button 
                      onClick={() => handleEditNews(item)}
                      className="text-blue-600 hover:text-blue-900"
                    >
                      编辑
                    </button>
                    <button 
                      onClick={() => handleDeleteNews(item.id)}
                      className="text-red-600 hover:text-red-900"
                    >
                      删除
                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );

  const renderUsers = () => (
    <div className="space-y-6">
      <div className="flex flex-col sm:flex-row justify-between items-start sm:items-center space-y-4 sm:space-y-0">
        <div>
          <h2 className="text-2xl font-bold text-gray-900">用户管理</h2>
          <p className="text-gray-600">管理系统用户和权限</p>
        </div>
        <button 
          onClick={handleAddUser}
          className="bg-purple-600 text-white px-4 py-2 rounded-lg hover:bg-purple-700 transition-colors flex items-center space-x-2"
        >
          <Plus className="w-4 h-4" />
          <span>添加用户</span>
        </button>
      </div>

      {/* Users Table */}
      <div className="bg-white rounded-lg shadow-md overflow-hidden">
        <div className="px-6 py-4 border-b border-gray-200">
          <h3 className="text-lg font-semibold text-gray-900">用户列表 ({users.length})</h3>
        </div>
        <div className="overflow-x-auto">
          <table className="min-w-full divide-y divide-gray-200">
            <thead className="bg-gray-50">
              <tr>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">用户名</th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">邮箱</th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">角色</th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">状态</th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">最后登录</th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">操作</th>
              </tr>
            </thead>
            <tbody className="bg-white divide-y divide-gray-200">
              {users.map((user) => (
                <tr key={user.id}>
                  <td className="px-6 py-4 whitespace-nowrap">
                    <div className="text-sm font-medium text-gray-900">{user.username}</div>
                  </td>
                  <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">{user.email}</td>
                  <td className="px-6 py-4 whitespace-nowrap">
                    <span className="px-2 inline-flex text-xs leading-5 font-semibold rounded-full bg-blue-100 text-blue-800">
                      {user.role === 'admin' ? '管理员' : user.role}
                    </span>
                  </td>
                  <td className="px-6 py-4 whitespace-nowrap">
                    <span className={`px-2 inline-flex text-xs leading-5 font-semibold rounded-full ${
                      user.status === 'active' ? 'bg-green-100 text-green-800' : 'bg-red-100 text-red-800'
                    }`}>
                      {user.status === 'active' ? '活跃' : '停用'}
                    </span>
                  </td>
                  <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">
                    {user.lastLogin ? user.lastLogin : '从未登录'}
                  </td>
                  <td className="px-6 py-4 whitespace-nowrap text-sm font-medium space-x-2">
                    <button 
                      onClick={() => handleEditUser(user)}
                      className="text-blue-600 hover:text-blue-900"
                    >
                      编辑
                    </button>
                    {user.id !== 1 && (
                      <button 
                        onClick={() => handleDeleteUser(user.id)}
                        className="text-red-600 hover:text-red-900"
                      >
                        删除
                      </button>
                    )}
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );

  const renderContacts = () => (
    <div className="space-y-6">
      <div className="flex flex-col sm:flex-row justify-between items-start sm:items-center space-y-4 sm:space-y-0">
        <div>
          <h2 className="text-2xl font-bold text-gray-900">联系管理</h2>
          <p className="text-gray-600">管理客户咨询和消息</p>
        </div>
      </div>
      <div className="bg-white rounded-lg shadow-md p-6">
        <p className="text-gray-600">联系管理功能将在这里实现...</p>
      </div>
    </div>
  );

  const renderSEO = () => (
    <div className="space-y-6">
      <div className="flex flex-col sm:flex-row justify-between items-start sm:items-center space-y-4 sm:space-y-0">
        <div>
          <h2 className="text-2xl font-bold text-gray-900">SEO设置</h2>
          <p className="text-gray-600">优化网站搜索引擎排名</p>
        </div>
      </div>
      <div className="bg-white rounded-lg shadow-md p-6">
        <p className="text-gray-600">SEO设置功能将在这里实现...</p>
      </div>
    </div>
  );

  const renderContent = () => {
    switch (activeTab) {
      case 'dashboard':
        return renderDashboard();
      case 'products':
        return renderProducts();
      case 'news':
        return renderNews();
      case 'users':
        return renderUsers();
      case 'contacts':
        return renderContacts();
      case 'seo':
        return renderSEO();
      default:
        return renderDashboard();
    }
  };

  return (
    <div className="min-h-screen bg-gray-50">
      {/* Header */}
      <div className="bg-white shadow-sm border-b border-gray-200">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="flex justify-between items-center py-6">
            <div>
              <h1 className="text-2xl font-bold text-gray-900">管理后台</h1>
              <p className="text-gray-600">Vanguard Machinery Trading Company</p>
            </div>
            <div className="flex items-center space-x-4">
              <button className="text-gray-500 hover:text-gray-700">
                <Settings className="w-5 h-5" />
              </button>
              <button className="text-gray-500 hover:text-gray-700">
                <BarChart3 className="w-5 h-5" />
              </button>
            </div>
          </div>
        </div>
      </div>

      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        <div className="flex flex-col lg:flex-row gap-8">
          {/* Sidebar */}
          <div className="lg:w-64 flex-shrink-0">
            <nav className="space-y-2">
              {menuItems.map((item) => {
                const Icon = item.icon;
                return (
                  <button
                    key={item.id}
                    onClick={() => setActiveTab(item.id)}
                    className={`w-full flex items-center space-x-3 px-4 py-3 rounded-lg text-left transition-colors ${
                      activeTab === item.id
                        ? `bg-${item.color}-100 text-${item.color}-700 border border-${item.color}-200`
                        : 'text-gray-700 hover:bg-gray-100'
                    }`}
                  >
                    <Icon className={`w-5 h-5 ${
                      activeTab === item.id ? `text-${item.color}-600` : 'text-gray-500'
                    }`} />
                    <span className="font-medium">{item.label}</span>
                  </button>
                );
              })}
            </nav>
          </div>

          {/* Main Content */}
          <div className="flex-1">
            {renderContent()}
          </div>
        </div>
      </div>
    </div>
  );
};

export default Admin;
